    /**
     * 获取k 数据
     */
    private void requestKline() {

        Retrofit retrofit = new Retrofit.Builder()
                .addConverterFactory(GsonConverterFactory.create())
                .addCallAdapterFactory(RxJavaCallAdapterFactory.create())//新的配置
                .baseUrl(Constants.KLineUrl)
                .client(client)
                .build();
        KLineSevser klineServer = retrofit.create(KLineSevser.class);
        klineServer.kline("btcusdt", "5min", "20")
                .subscribeOn(Schedulers.newThread())//请求在新的线程中执行
                .observeOn(Schedulers.io())         //请求完成后在io线程中执行
                .doOnNext(new Action1<KLineInfo>() {
                    @Override
                    public void call(KLineInfo klineinfo) {
//                        saveUserInfo(userInfo);//保存用户信息到本地
                    }
                })
                .observeOn(AndroidSchedulers.mainThread())//最后在主线程中执行
                .subscribe(new Subscriber<KLineInfo>() {
                    @Override
                    public void onCompleted() {

                    }

                    @Override
                    public void onError(Throwable e) {
                        //请求失败
                        LogUtils.i(e.toString());
                    }

                    @Override
                    public void onNext(KLineInfo klineinfo) {
                        //请求成功
                        if (klineinfo != null) {
                            String status = klineinfo.getStatus();
                            if (status.equals("ok")) {
                                List<KLineInfo.DataBean> data = klineinfo.getData();
                                int size = data.size();
                                for (int i = 0; i < size; i++) {
                                    KLineInfo.DataBean dataBean = data.get(i);
                                    SimpleDateFormat simpleDateFormat = new SimpleDateFormat("hh:mm");
                                    int id = dataBean.getId();
                                    Date date = new Date(id);
                                    String format = simpleDateFormat.format(date);
                                    xList.add(format);
                                    yVals1.add(i, new CandleEntry(i, dataBean.getHigh(), dataBean.getLow(), dataBean.getOpen(), dataBean.getClose()));
                                    yValue.add(new Entry(i, dataBean.getHigh()));
                                }
                                showChart(canleChart, getCandleData(yVals1), getLineData(yValue, "折线图", rgb));
                            } else if (status.equals("error")) {
                                LogUtils.i("请求失败!");
                            }
                        }
                    }
                });
    }


    /**
     * @param candleData
     */
    public void showChart(CombinedChart combinedChart, CandleData candleData, LineData linedata) {
        CombinedData combinedData = new CombinedData();
        combinedData.setData(candleData);
        combinedData.setData(linedata);
        combinedChart.setDoubleTapToZoomEnabled(false);
        combinedChart.setBackgroundColor(Color.argb(12, 12, 22, 22));
        //横坐标
        setChartXAxis(combinedChart, xList);
        setChartYAxis(combinedChart);
        combinedChart.setData(combinedData);
        combinedChart.setPinchZoom(true);//比例缩放
        combinedChart.moveViewToX(yVals1.size() - 1);
        if (isAnimatex) {
            combinedChart.animateX(2500);
        }


    }

